// 1. Importar 
// const { ApolloServer, gql } = require('apollo-server')

import {  ApolloServer, gql } from 'apollo-server'


// 4. Crear Type Definition 
// Dentro de typeDefs colocaremos todos nuestro type definitions
// Query nos permite hacer consulta en nuestra bd 
// Definimos nuestra segunda función


const typeDefs = gql`

    type Curso { 
        titulo: String
    }

    type Tecnologia {
        tecnologia: String
    }

    type Query { 
        obtenerCursos : [Curso]

        obtenerTecnologia: [Tecnologia]
    }

`

const cursos = [
    {
        titulo: 'JavaScript Moderno Guía Definitiva Construye +10 Proyectos',
        tecnologia: 'JavaScript ES6',
    },
    {
        titulo: 'React – La Guía Completa: Hooks Context Redux MERN +15 Apps',
        tecnologia: 'React',
    },
    {
        titulo: 'Node.js – Bootcamp Desarrollo Web inc. MVC y REST API’s',
        tecnologia: 'Node.js'
    }, 
    {
        titulo: 'ReactJS Avanzado – FullStack React GraphQL y Apollo',
        tecnologia: 'React'
    }
];

// 5. Resolver
// Lo que tengamos en Query en TypeDefs tambein debe estar en nustro resolver 
// Los resolver son funciones 

const resolvers = {
    Query: {
        obtenerCursos: () => cursos,
        // Segunda funcion 
        obtenerTecnologia: () => cursos
    }
}

// 2. Crear servidor 
const server = new ApolloServer({typeDefs, resolvers}) // Colocamos en orden nuestro typeDefs y resolvers

// 3.  Arrancar servidor de apollo 
server.listen().then(({ url }) => {
    console.log(`Server listo en la url ${url}`)
})

